CREATE TABLE sentences (
  id INT NULL AUTO_INCREMENT,
  course INT NULL,
  unit INT NULL,
  unit_title VARCHAR(191) NULL,
  phrase VARCHAR(191) NULL,
  answer1 VARCHAR(191) NULL,
  answer2 VARCHAR(191) NULL,
  answer3 VARCHAR(191) NULL,
  answer4 VARCHAR(191) NULL,
  correct VARCHAR(191) NULL,
  version INT NULL,
  image VARCHAR(191) NULL,
  exercise INT NULL,
  PRIMARY KEY (id));

